   public function buscaPosts()
   {
        $DB_HOST=$_ENV["DB_HOST"];
        $DB_TABLE=$_ENV["DB_TABLE"];
        $DB_DATABASE=$_ENV["DB_DATABASE"];
        $DB_USERNAME=$_ENV["DB_USERNAME"];
        $DB_PASSWORD=$_ENV["DB_PASSWORD"];

        $query = "
            SELECT
                p.id AS 'post_id',
                p.urlamigavel AS 'loc',
                pi.frase,
                pi.nomeDownload,
                pi.frase_id,
                p.updated_at AS 'changefreq'
            FROM
                pandora_fraseteca.posts p
            INNER JOIN (
                SELECT
                    post_id,
                    mostraimg,
                    frase_id ,
                    frases.frase,
                    frases.nomeDownload,
                    ROW_NUMBER() OVER (PARTITION BY post_id ORDER BY posts_itens.id) AS row_num
                FROM
                    pandora_fraseteca.posts_itens
                inner join pandora_fraseteca.frases on posts_itens.frase_id = frases.id
                WHERE
                    mostraimg = '1' and
                    frase_id != 1 and
                    nomeDownload is not null
            ) pi ON pi.post_id = p.id
            WHERE
                p.status = 1
                AND p.tipo = 1
                AND pi.row_num <= 5;

        ";
        $conn = new mysqli($DB_HOST, $DB_USERNAME, $DB_PASSWORD, $DB_DATABASE);
        if ($conn->connect_error) {
            die("Falha na conexÃ£o: " . $conn->connect_error);
        }

        // Executa a consulta
        $data = $conn->query($query);
        if($data):
            return $data;
        else :
            return null;
        endif;
   }

    public function formatarData($dataHora) {
        // Cria um objeto DateTime a partir da string recebida
        $data = DateTime::createFromFormat('Y-m-d H:i:s', $dataHora);

        // Verifica se a criaÃ§Ã£o do objeto DateTime foi bem-sucedida
        if ($data === false) {
            return "Formato de data invÃ¡lido.";
        }

        // Retorna a data formatada no formato desejado
        return $data->format('Y-m-d\TH:i:sP');
    }


