    public function validaImagemDaFrase($file)
    {
        # code...
        $exists = false;

        if ($file) :
            $arquivo_valido = \storage_path() . "/app/public/frases/" . str_replace('/storage/frases/', '', $file);

            $quebra = explode(".jpg", $arquivo_valido);

            if (isset($quebra)) {
                if (file_exists($quebra[0] . '.jpg'))
                    $exists = true;
                else
                    $exists = false;
            }
        endif;
        return $exists;
    }
